_email            = re.compile("([a-z0-9!#$%&'*+\/=?^_`{|.}~-]+@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?)", re.IGNORECASE)
_ip               = re.compile('(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)', re.IGNORECASE)
_ipv6             = re.compile('\s*(?!.*::.*::)(?:(?!:)|:(?=:))(?:[0-9a-f]{0,4}(?:(?<=::)|(?<!::):)){6}(?:[0-9a-f]{0,4}(?:(?<=::)|(?<!::):)[0-9a-f]{0,4}(?:(?<=::)|(?<!:)|(?<=:)(?<!::):)|(?:25[0-4]|2[0-4]\d|1\d\d|[1-9]?\d)(?:\.(?:25[0-4]|2[0-4]\d|1\d\d|[1-9]?\d)){3})\s*', re.VERBOSE|re.IGNORECASE|re.DOTALL)
_credit_card      = re.compile('((?:(?:\\d{4}[- ]?){3}\\d{4}|\\d{15,16}))(?![\\d])')
_btc_address      = re.compile('(?<![a-km-zA-HJ-NP-Z0-9])[13][a-km-zA-HJ-NP-Z0-9]{26,33}(?![a-km-zA-HJ-NP-Z0-9])')
_street_address   = re.compile('\d{1,4} [\w\s]{1,20}(?:street|st|avenue|ave|road|rd|highway|hwy|square|sq|trail|trl|drive|dr|court|ct|park|parkway|pkwy|circle|cir|boulevard|blvd)\W?(?=\s|$)', re.IGNORECASE)
_zip_code         = re.compile(r'\b\d{5}(?:[-\s]\d{4})?\b')
_po_box           = re.compile(r'P\.? ?O\.? Box \d+', re.IGNORECASE)
_ssn              = re.compile('(?!000|666|333)0*(?:[0-6][0-9][0-9]|[0-7][0-6][0-9]|[0-7][0-7][0-2])[- ](?!00)[0-9]{2}[- ](?!0000)[0-9]{4}')

def detect(data):
    return {
        "phone": _phone.findall(data),
        "phones_with_exts": _phones_with_exts.findall(data),
        "link": _link.findall(data),
        "email": _email.findall(data),
        "ip": _ip.findall(data),
        "ipv6": _ipv6.findall(data),
        "credit_card": _credit_card.findall(data),
        "btc_address": _btc_address.findall(data),
        "street_address": _street_address.findall(data),
        "zip_code": _zip_code.findall(data),
        "po_box": _po_box.findall(data),
        "ssn": _ssn.findall(data)
    }

def redact(data, target='all'):
    detected = detect(data)

    for pii_type in detected:
        if pii_type != target and target != 'all':
            continue

        pii_data = detected[pii_type]
        
        if len(pii_data) == 0:
            continue
        
        for pii_snippet in pii_data:
            data = data.replace(pii_snippet, "[REDACTED]")
    
    return data

phrase = "Hello there 435-354-2324 628-32-3816"

print(redact("Hello there 435-354-2324 628-32-3816"))